  inline const seg_configConfigStatics *seg_configConfig::__get_statics__()
  {
    const static seg_configConfigStatics *statics;
  
    if (statics) // Common case
      return statics;

    boost::mutex::scoped_lock lock(dynamic_reconfigure::__init_mutex__);

    if (statics) // In case we lost a race.
      return statics;

    statics = seg_configConfigStatics::get_instance();
    
    return statics;
  }


